"use strict";
(function(a) {
    a(document).ready(function() {
        a(".tmp-toggle, .tmp-overlay .tmp-tile li.c", "#tile-menu-pro").on("click", function(c) {
            c.preventDefault();
            var b = a(".tmp");
            var d = a(".is-tmp");
            b.toggleClass("tmp-active");
            if (b.hasClass("tmp-push")) {
                d.toggleClass("tmp-push-active")
            }
            if (b.hasClass("tmp-blur")) {
                d.toggleClass("tmp-blur-active")
            }
            if (b.hasClass("tmp-opacity")) {
                d.toggleClass("tmp-opacity-active")
            }
        });
        a(".tmp-widget-toggle").on("click", function(b) {
            b.preventDefault();
            a(a(this).data("target")).slideToggle(200)
        })
    })
}(jQuery));